	/** ajax的post公共调用方法 **/
	post: function(option, callback) {
		$.ajax({
			type:"POST",
			async:(!!option.async)?false:true,
			url:option.url,
			data:option.data,
			success: function(msg) {
				!!callback==true?callback(msg):"";
			}
		})
	},
	post2: function(option, callback) {
		$.post(option.url, option.data, function(msg) {
			!!callback==true?callback(msg):"";
		})
	}
